import axios  from "axios";


let myUrl='http://localhost:4002/'
//קבלת כל המןצרים
export const getAllProduct=async()=>{
    let allproduct = await axios.get(`${myUrl}Products`)
    console.log(allproduct.data);
    return allproduct.data;
}

//קבלת מוצר ע"י קוד
export const getProductById=async(id)=>{
    console.log(id)
    let product = await axios.get(`${myUrl}Products/${id}`)
    
    return product.data;
}
//הוספת מוצר חדש
export const addProduct=async(products)=>{
    let addpro =await axios.post(`${myUrl}Products/`,products)
    return addpro.data;
}
// לפי קוד מחיקת מוצר
export const deletProduct =async(id)=>{
  let delProduct = await axios.delete(`${myUrl}Products/ ${id}`)
    return delProduct.data;
}
