//                 products:[]
    
//             }
//             let NuserList = await AddUser(newUser)
           
//             console.log(NuserList)
//             //אחרי הרשמה מעביר לדף מוצרים
//             history.push('/ListProduct');
//         }
    

   
     return<>
    <form onSubmit={(e)=>addNewUser(e)}>
        <h3>הרשמה</h3>
        <div>
        <input type="text" placeholder="מס' זהות" id="tz" ></input>
       <br></br>
       </div>
       <div>
        <input type="text" placeholder="שם מלא" id="name" onChange={(e)=>validateName(e)}></input>
        <small style={{ color: "red" }}>{errors.UserNameError}</small>
        </div>
        <div>
        <input type="tel" placeholder="טלפון" id="tel" ></input>
        </div>
        <div>
        <input type="email" placeholder="כתובת דוא'ל" id="mail" onChange={(e)=>validateEmail(e)}></input>
        <small style={{ color: "red" }}>{errors.EmailError}</small>
        </div>
        <div>
        <input type="password" placeholder="סיסמא" id="pass" onChange={(e)=>validatePass(e)}></input>
        <small style={{ color: "red" }}>{errors.PasswordError}</small>
        </div>
        <br></br>
         <div>
        <input type="submit" value="אישור" style={{height:"30px",width:"70px",color:"green"}}/>
        </div>
    </form>
   
           </>
}

