//קומפוננטה
export const ListProduct=()=>{

let [myprod,setmyprod ] = useState()
//פונקציה שמורה שמציגה את הנתונים בעת טעינת הדף
  useEffect(
   async () => {
     //משתנה שמכניס בתוכו את כל המsetmyprodוצרים
    let getPro =await getAllProduct ();
    console.log(getPro)
    setmyprod(getPro)
   
   },
   []
  )
   
