  <table>
        <tr>
            <th>קוד מוצר</th>
            <th>שם מוצר</th>
            <th>תמונת מוצר</th>
            <th></th>
        </tr>
        { 
            myprod != undefined && 
            myprod.length > 0 &&
            myprod.map((item) =>
            <tr>
                <td>{item.id}</td>  
                <td>{item.name}</td>  
                {pic != undefined &&
                pic.length>0 &&
                <td><img src={pic[item.id - 1]} style={{ width: "200px" }} alt="not"></img> </td> }   
               <td><input type="button" value="פרטים" onClick={()=>prodDatalies(item)}></input></td>
               
            </tr>
            )
        }
        
    </table>
    </>
}
