import { Link } from "react-router-dom"
import { useDispatch } from "react-redux";
import { addBasket } from "../readux/basketAction";
import { useSelector } from "react-redux";

export const ProductData = (props) => {
    let myDisatch = useDispatch();
    let prod = props.location.state.user;
    let pic=useSelector((store)=>{return store.PictureArr})
    const addToBasket = () => {
        debugger
        myDisatch(addBasket(prod))
        console.log("נכנס?")
    }
    return <>
        <Link to="/basket">🛒</Link>
        {/* <lable onClick="goToBasket()"></lable> */}
        <br></br>
        {pic != undefined &&
                pic.length>0 &&
                <img src={pic[prod.id - 1]} style={{ width: "200px" }} alt="not"></img>  }   
                <br></br>
        <lable>קוד מוצר</lable><p>{prod.id}</p>
        <lable>שם מוצר</lable><p>{prod.name}</p>
        <lable>כמות במלאי</lable><p>{prod.amount}</p>
        <lable>קוד קטגוריה</lable><p>{prod.idCategory}</p>
        <lable>מחיר</lable><p>{prod.price}</p>
        <input type="button" value="הוספה לסל" onClick={addToBasket}></input>
        <br></br>
        <Link to="/products">חזור לעמוד למוצרים</Link>
        {/* <input type="button" value="חזור לעמוד למוצרים" onClick={/products}></input>
    <input type="button" value="הוספה לסל "></input> */}
    </>
}
