//כניסה
//אם המשתמש אכן קיים במערכת אז הוא יעבור לרשימת המוצרים
import { EnterUser } from "../thanks/ThankUser";


export const EnterFrom = ({history}) => {
    
    //שולחת את הנתנים שקיבל לשרת
    const loginf= async(e) =>{
        e.preventDefault();
      //מכניס לתוכו את הנתונים בשביל לשלוח לשרת
        let eUser={
            mail: e.target.mail.value,
            Password: e.target.pass.value
        }

        console.log(eUser);
    //שולח את הנתונים לשרת
        let checkUser= await EnterUser(eUser);
        //  בודק אם הנתונים שהוא מכניס קיימים בשרת        
        //  if(checkUser!=undefined && checkUser.name=="User is not found!!")
        if(checkUser!=undefined && checkUser.name=="product not found")
        history.push("/Harshama")
          else
          history.push("/ListProduct")
      }
    return<>
   {/* כאשר לוחצים על הכפתור של הכניסה הוא מהעביר אותי לפונקציה ששולחת אותו לקרת ובודקת האם הוא עונה לתנאים */}
    <form onSubmit={(e)=>loginf(e)}>
        <h3>כניסה</h3>
        <p>כתובת מייל</p>
        <input type="email" id="mail"></input>
        <br></br>
        <p>סיסמא</p>
        <input type="password" id="pass"></input>
        <br></br>
        <input type="submit" value="כניסה"></input>
 
    </form>
    </>
   

 }
