import axios  from "axios";

const myUrl='http://localhost:4002/'

//הצטרפות-הוספת משתמש
export const AddUser=async(newUser)=>{
    debugger
    let allMyUsersAfterAdding = await axios.post(`${myUrl}user/register`, newUser)
    console.log(allMyUsersAfterAdding.data);
    return allMyUsersAfterAdding.data;
}
//בדיקה אם קיים
export const EnterUser=async(User)=>{
    
    let userName = await axios.post(`${myUrl}user/login`, User)
    console.log(userName.data);
    return userName.data;
}
//קבלת משתמשים מהשרת
export const GetUsers=async()=>{
    
    let users = await axios.get(`${myUrl}users`)
    console.log(users.data);
    return users.data;
}
