import { useState } from "react"
import { Link } from "react-router-dom"
import { AddUser } from "../thanks/thankRegistration"

export const Registration=({history})=>{
    const [data1,setData]=useState(
     {   
        // tz:"",
        // name:"",
        // fone:"",
        // email:"",
        // password:""
    } )


    const myFunc=async (e)=>{
        
        e.preventDefault();
        // console.log(e.target.tz.value);
        // console.log(e.target.name.value);
        // console.log(e.target.tel.value);
        // console.log(e.target.mail.value);
        // console.log(e.target.pass.value);

        //אובייקט המכיל את מה שהמשתמש הכניס
        let objUser={
            name:e.target.name.value,
            mail:e.target.mail.value,
            password:e.target.pass.value,
            products:[]
        }

      
        setData(objUser)
        console.log(data1)

        //קריאה לפונקציה שניגשת לשרת ומוסיפה משתמש
        let newListUsers = await AddUser(objUser);
        console.log(newListUsers)
        history.push("/products")
      
    }
 return<>
    <form onSubmit={(e)=>myFunc(e)}>
        <h3>הרשמה</h3>
        <input type="text" placeholder="מס' זהות" id="tz" ></input>
        <br></br>
        <input type="text" placeholder="שם מלא" id="name" ></input>
        <br></br>
        <input type="tel" placeholder="טלפון" id="tel" ></input>
        <br></br>
        <input type="email" placeholder="כתובת דוא'ל" id="mail" ></input>
        <br></br>
        <input type="password" placeholder="סיסמא" id="pass" ></input>
        <br></br>
        <input type="submit" value="אישור" style={{height:"30px",width:"70px",color:"green"}}/>
    </form>
    </>
}
