import axios  from "axios";

const myUrl='http://localhost:4002/'

//קבלת רשימה של כל המשתמשים
export const GetAllUsers = async () =>{
  //הכנסת רשימת של המשתמשים שהתקבלו מהשרת
let userr = await axios.get(`${myUrl}users`);
  //החזרת הנתונים בקומפוננטה הנוכחית
  console.log(userr.data)

  return userr.data;
 
}

// הצטרפות של משתמש חדש
export const AddUser=async(newUser)=>{
    debugger
    //פהוספת המשתמש החדש בשרת וקבלת הרשימה של המשתמשים המעודכנת לאחר הוספה
  let addUser = await axios.post(`${myUrl}user/register`, newUser)
    //החזרת הנתונים של המשתמש החדש לקומפוננטה
    return addUser.data;
}
//בודק בקומפוננטה הכנסה האם אותו משתמש קיים במערכת
export const EnterUser=async(User)=>{
    //בודק בשרת האם קיים
    let userName = await axios.post(`${myUrl}user/login`, User)
   // החזרת הנתונים של המשתמש לקומפוננטה הנוכחיתש 
    return userName.data;
}
