import { useState } from "react";
import { AddUser } from "../thanks/ThankUser";

//הרשמה

export const HarshamaFrom = ({history}) => {

    // const [data1,setData]=useState({})

//אתחול מאפייני האובייקט בסטייט בערכים התחלתיים
     const [errors, setErrors] = useState(
        {
            UserNameError: "",
            PasswordError: "",
            EmailError: ""
        })

         //שומר את ערכי השדות 
    const [myUser, setMyUser] = useState(
    //      //בטופס הוספה- אין לנו נתונים קודמים על המשתמש
    //     //ולכן נגדיר אובייקט ריק
    //     //שבתוכו יוצבו אח"כ כל הנתונים שתקינים שהוזנו
    //     !props.userToEdit?
    //     {}
    //     :
    //     //כלומר,אנחנו בטופס עדכון
    //     //ויש לנו מידע קודם על המשתמש
    //     //את המידע נציג בשדות הקלט
    //     //ונאפשר עריכה
    //    props.userToEdit
       
    )
    // שם מלא
    const validateName = (event) => {
        event.preventDefault();
        if (!event.target.value)
            setErrors({ ...errors, UserNameError: "שדה זה חובה" })
    }   

    //מייל
    const validateEmail = (event) => {
        event.preventDefault();
         if (!event.target.value) {
                 setErrors({ ...errors, EmailError: "שדה זה חובה" });
                }
         else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(event.target.value)) {
                    setErrors({ ...errors, EmailError: 'כתובנת האמייל לא תקינה' });
                }
        
            }
                //סיסמה
                const validatePass = (event) => {
                    event.preventDefault();
                    var passW = /^[A-Za-z]\w{7,14}$/;
                    if (!event.target.value)
                        setErrors(
                            {
                                ...errors,
                                PasswordError: "שדה זה חובה"
                            })
                    else if (event.target.value.match(passW)) {
                        setErrors({ ...errors, PasswordError: "יפה" })
                        setMyUser({ ...myUser, passUser: event.target.value })
                    }
                    else
                        setErrors({ ...errors, PasswordError: 'הסיסמה חייבת להיות בין 7 ל-16 תווים המכילים רק תווים, ספרות מספריות, קו תחתון. והתו הראשון חייב להיות אות' })
              } 
           
            // //נותנת אפשרות שיראו את הטקסט
        const addNewUser=async(e)=> {
            debugger
                e.preventDefault();
            let newUser ={
                name:e.target.name.value,
                mail:e.target.mail.value,
                password:e.target.pass.value,
                products:[]
    
            }
            let NuserList = await AddUser(newUser)
           
            console.log(NuserList)
            //אחרי הרשמה מעביר לדף מוצרים
            history.push('/ListProduct');
        }
    

   
     return<>
    <form onSubmit={(e)=>addNewUser(e)}>
        <h3>הרשמה</h3>
        <div>
        <input type="text" placeholder="מס' זהות" id="tz" ></input>
       <br></br>
       </div>
       <div>
        <input type="text" placeholder="שם מלא" id="name" onChange={(e)=>validateName(e)}></input>
        <small style={{ color: "red" }}>{errors.UserNameError}</small>
        </div>
        <div>
        <input type="tel" placeholder="טלפון" id="tel" ></input>
        </div>
        <div>
        <input type="email" placeholder="כתובת דוא'ל" id="mail" onChange={(e)=>validateEmail(e)}></input>
        <small style={{ color: "red" }}>{errors.EmailError}</small>
        </div>
        <div>
        <input type="password" placeholder="סיסמא" id="pass" onChange={(e)=>validatePass(e)}></input>
        <small style={{ color: "red" }}>{errors.PasswordError}</small>
        </div>
        <br></br>
         <div>
        <input type="submit" value="אישור" style={{height:"30px",width:"70px",color:"green"}}/>
        </div>
    </form>
   
           </>
}
