import { useEffect, useState } from "react";
import { GetAllUsers } from "../thanks/ThankUser";

export const ShowUser =()=>{
    
    let [myUsers, setMyUsers] = useState()
   //בזמן טיענת הדף זה יציג על המסך
    useEffect(async()=>{
    //מגדירה משתנה שאליו נכנס הנתונים מהשרת
    let ListUsers =await GetAllUsers();
    console.log(ListUsers)
    setMyUsers(ListUsers)
    }
    );

    return<>
    <p>כל המשתמשים</p>
    <table>
        <tr>
            <th>קוד של המשתמש</th>
            <th>שם של המשתמש</th>
            <th>סיסמא של המשתמש</th>
        </tr>
        {
            myUsers!=undefined && myUsers.lengh>0 && myUsers.map((item)=>
            <tr>
                <td>{item.id}</td>
                <td>{item.name}</td>
                <td>{item.password}</td>
               
            </tr>
            )}
    </table>
   
}
</>
}
