import { useEffect } from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";

export const Basket = () => {
    let pic=useSelector((store)=>{return store.PictureArr})

    let amount=1
    let basket = useSelector((store) => {
        debugger
        return store.MyBasket
    });
    const more=()=>{
        amount=amount+1;
    }
    const less=()=>{
        amount=amount-1;
    }
    //גישה לסטור הגלובאלי
    //והצגת הנתוני םשהמורים בסל קניות

    //useDispath

    //הגדרת משתנה שיקבל לתוכו את הנתונים מהסטור
    return <>
        <p>🛒</p>
        <p>סל הקניות שלי</p>
        <table>
            <tr>
                <th>שם מוצר</th>
                <th>קוד מוצר</th>
                <th>מחיר</th>
                <th>כמות מוצר</th>
                <th>תמונה</th>
                
            </tr>
            {
                basket != undefined &&
                basket.map((item) =>
                    <tr>
                        <td>{item.name}</td>
                        <td>{item.id}</td>
                        <td>{item.price}</td>
                        <td>{item.amount}</td>
                        <th><input type="button" value="+" onClick={more}></input></th>
                        <th><input type="button" value="-" onClick={less}></input></th>
                        {pic != undefined &&
                pic.length>0 &&
                <td><img src={pic[item.id - 1]} style={{ width: "200px" }} alt="not"></img> </td> }   
                    </tr>
                )
            }
        </table>
        <Link to="/products">חזור לעמוד למוצרים</Link>
    </>
}
