import { useEffect ,useState} from "react"
import { useSelector } from "react-redux"
import {getAllProduct, getProductById} from "../thanks/thankProduct"
import {getcategoryById} from "../thanks/thankCategory"

export const ProductList =({history})=>{
    let prodId;
    let  allProd
    //let  allProd = await getAllProduct();
    let pic=useSelector((store=>{return store.PictureArr}))

    let [myprods, setmyprods] = useState()
    let m=myprods;
    console.log(m+"kkkkkkkk")
    //בעת טעינת הדף מציג מוצרים
    useEffect(
        async () => {
            //קריאת שרת ושמירת הנתונים 
            allProd = await getAllProduct();
                console.log(allProd)
         debugger
                setmyprods(allProd)
        },
        [])
    // useEffect(()=>
    //     async()=>{
    //         debugger
    //     allProd = await getAllProduct();
    //     console.log(allProd)
    //     debugger
    //     setmyprods(allProd)
    //     },[] );

    //   const  f=async()=>{
    //     allProd = await getAllProduct();
    //     console.log(allProd)
    //     debugger
    //     setmyprods(allProd)
    //     };
   
//פונקציה שמראה פרטים על המוצר
        const prodDatalies=(item1)=>{
             history.push({pathname:'/productData',state:{user:item1}})
        }
//מציג את כל המוצרים
        const allProds=async()=>{
                allProd = await getAllProduct();
                console.log(allProd)
                debugger
                setmyprods(allProd)
                };
//מציג לפי בחירת קטגורית בית
        const homeProds=async(id)=>{
            allProd = await getAllProduct();
            console.log(id)
            debugger 
            console.log(m);
          let sort = allProd.filter(p=>p.idCategory==id)
           // prodId=await getProductById(id)
            console.log(sort)
            setmyprods(sort)
            
        }
//מציג לפי בחירת קטגורית חדר ילדים
        const boyRoomProds=async(id)=>{
            allProd = await getAllProduct();
            console.log(id)
            debugger 
            let sort = allProd.filter(p=>p.idCategory==id)
            console.log(sort)
            debugger
            setmyprods(sort)
            debugger
        }
//מציג לפי בחירת קטגורית חצר
        const garden=async(id)=>{
            allProd = await getAllProduct();
            console.log(id)
            debugger 
            let sort = allProd.filter(p=>p.idCategory==id)
            console.log(sort)
            setmyprods(sort)
        }
        
//מציג לפי בחירת קטגורית חדר רחצה
        const bathroom=async(id)=>{
            allProd = await getAllProduct();
            console.log(id)
            debugger 
            let sort = allProd.filter(p=>p.idCategory==id)
            console.log(sort)
            setmyprods(sort)
        }

    return<>
    <p>כל המוצרים שלנו</p>
    {/* <input type="button" value="fff" onClick={()=>f()}></input>  */}
    <input type="button" value="הכל" onClick={allProds}></input>
    <input type="button" value="בית" onClick={()=>homeProds(1)}></input>
    <input type="button" value="חדר ילדים" onClick={()=>boyRoomProds(3)}></input>
    <input type="button" value="חצר" onClick={()=>garden(2)}></input>
    <input type="button" value="חדר רחצה" onClick={()=>bathroom(4)}></input>
    <table>
        <tr>
            <th>קוד מוצר</th>
            <th>שם מוצר</th>
            <th>תמונת מוצר</th>
            <th></th>
        </tr>
        { 
            myprods != undefined && 
            myprods.length > 0 &&
            myprods.map((item) =>
            <tr>
                <td>{item.id}</td>  
                <td>{item.name}</td>  
                {pic != undefined &&
                pic.length>0 &&
                <td><img src={pic[item.id - 1]} style={{ width: "200px" }} alt="not"></img> </td> }   
               <td><input type="button" value="פרטים" onClick={()=>prodDatalies(item)}></input></td>
            </tr>
            )
        }
        
    </table>
    </>

}
