import { EnterUser } from "../Componnet/ThankEnrollmant"
//כניסה
//אם הוא לוקח אז מכניסה הוא יעבור לקומפוננטה שבה יכול לבחור האם לפנות 
//לאיפה רוצה להכנס האם לצפות במוצרים וכו


export const EnterFrom = (history) => {
    
    const myFuncE= async(e) =>{
        e.preventDefault();
        let objData={

            mail: e.target.mail.value,
            Password: e.target.pass.value
        }

        console.log(objData);
    
        let checkUser= await EnterUser(objData);
          console.log(checkUser)
         
          if(checkUser!=undefined && checkUser.name=="User is not found!!")
          history.push("/Harshama")
          else
          history.push("/Harshama")
      }
    return<>
    
    <form onSubmit={(e)=>myFuncE(e)}>
        <h3>כניסה</h3>
        <p>כתובת מייל</p>
        <input type="email" id="mail"></input>
        <br></br>
        <p>סיסמא</p>
        <input type="password" id="pass"></input>
        <br></br>
        <input type="submit" value="כניסה"></input>
 
    </form>
    </>
   

 }
