import { useState } from "react"

//אתחול 
export const HarshamaFrom = () => {

    const [errors, setErrors] = useState(
        {
            FullName: "",
            UserName: "",
            Pon: "",
            Email: "",
            Password: ""
        })


    //שומר את ערכי השדות 
    //משנה שקובע האם להציג את הטופס
    const [isShow, setIsShow] = useState(false)

    //שם מלא
    const validateFullName = (event) => {
        event.preventDefault();
        if (!event.target.value)
            setErrors({ ...errors, FullName: "שדה זה חובה" })
        else if (event.target.value.includes(" "))
            setErrors({ ...errors, UserNameError: "שם משתמש לא יכול לכלול רווח" })
        else {
            setErrors({ ...errors, UserNameError: "Correct!!" })
            setMyUser({ ...myUser, userFullName: event.target.value })
        }

    }
        //שם משתמש

        const validateUserName = (event) => {
            event.preventDefault();
            if (!event.target.value)
                setErrors({ ...errors, UserNameError: "Required!!" })
            else if (event.target.value.includes(" "))
                setErrors({ ...errors, UserNameError: "שם משתמש לא יכול לכלול רווח" })
            else {
                setErrors({ ...errors, UserNameError: "Correct!!" })
                setMyUser({ ...myUser, userName: event.target.value })
            }
        }

            //טלפון

            const validatePon = (event) => {
                event.preventDefault();
                if (!event.target.value) {
                    setErrors({ ...errors, Pon: "נדרש" })
                    //איך כותבים את זה
                    //  else if (event.target.value.includes>9 && event.target.value.includes<9)
                    //setErrors({ ...errors,  Pon: 'שדה זה חייב להכיל 9 ספרות' })
                    // else{
                    //   setErrors({ ...errors,  Pon: "Correct!!" })
                    //   setMyUser({ ...myUser,  PonUser: event.target.value })
                }
            }
            //מייל

            const validateEmail = (event) => {
                event.preventDefault();
                if (!event.target.value) {
                    setErrors({ ...errors, EmailError: "שדה זה חובה" });
                }
                else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(event.target.value)) {
                    setErrors({ ...errors, EmailError: 'כתובנת האמייל לא תקינה' });
                }
                else {
                    setErrors({ ...errors, Email: "Correct!!" });
                    setMyUser({ ...myUser, EmailUser: event.target.value })
                }
            }
                //סיסמה
                const validatePass = (event) => {
                    event.preventDefault();
                    var passW = /^[A-Za-z]\w{7,14}$/;
                    if (!event.target.value)
                        setErrors(
                            {
                                ...errors,
                                Password: "שדה זה חובה"
                            })
                    else if (event.target.value.match(passW)) {
                        setErrors({ ...errors, Password: "נכון" })
                        setMyUser({ ...myUser, passUser: event.target.value })
                    }
                    else
                        setErrors({ ...errors, PasswordError: 'הסיסמה חייבת להיות בין 7 ל-16 תווים המכילים רק תווים, ספרות מספריות, קו תחתון. והתו הראשון חייב להיות אות' })
                }
         
            //נותנת אפשרות שיראו את הטקסט
            function funcev(event) {
                event.preventDefault();
                setIsShow(true);
            }
            return <>
                <form onSubmit={(e) => funcev(e)}>
                    <h1>טופס הרשמות</h1>
                    <div>
                        <p>הכנס את שם המלא שלך</p>
                        <input type="text" onChange={(e) => validateFullName(e)} />
                        <small style={{ color: "red" }}>{errors.FullName}</small>
                    </div>

                    <div>
                        <p>הכנס את שם המשתמש שלך</p>
                        <input type="text" onChange={(e) => validateUserName(e)} />
                        <small style={{ color: "red" }}>{errors.UserNameError}</small>
                    </div>

                    <div>
                        <p>הכנס את מספר הטלפון שלך</p></div>
                    <div>
                        <input type="text" onChange={(e) => validatePon(e)} />
                        <small style={{ color: "red" }} >{errors.Pon}</small>
                    </div>

                    <div>
                        <p>הכנס את האמייל שלך</p>
                        <input type="Email" onChange={(e) => validateEmail(e)} />
                        <small style={{ color: "red" }} >{errors.Email}</small>
                    </div>

                    <div>
                        <p>הכנס את הסיסמה שלך</p>
                        <input type="password" onChange={(e) => validatePassW(e)} />
                        <small style={{ color: "red" }}>{errors.Password}</small>
                    </div>
                </form>

                {
                    isShow === true && <div>
                        <h1>פרטי הנרשם</h1>
                        <p>{myUser.userFullName}</p>
                        <p>{myUser.userName}</p>
                        <p>{myUser.PonUser}</p>
                        <p>{myUser.EmailUser}</p>
                        <p>{myUser.passUser}</p>
                    </div>
                }
            </>
    
            }
